        public JobApplicationProcessorTests()
        {
            _jobApplicationProcessor = new JobApplicationProcessor();
        }

        [Test]
        public void GetJobFromJsonInputReturnNullValueDueToEmptyParameter()
        {
            var result = _jobApplicationProcessor.GetJobFromJsonInput(string.Empty);
            Assert.IsNull(result);
        }

        [Test]
        public void GetJobFromJsonInputReturnNullValueDueToWrongFormat()
        {
            var jobInfoFromJson = "[{Id:\"1\"}]";
            var result = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNull(result);

            jobInfoFromJson = "[{Id:\"ThisCausesAnExceptionSinceImNotANumber\"}]";
            Assert.That(() => _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson), Throws.Nothing);
            result = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNull(result);
        }

        [Test]
        public void GetJobFromJsonInputReturnJobWithOnlyOneQuestionDueToDuplicateId()
        {
            var jobInfoFromJson = "[{\"Id\":\"1\",\"Question\":\"Do you have a car?\",\"Answer\":\"yes\"},{\"Id\":\"1\",\"Question\":\"Are you legally authorized to work in the USA?\",\"Answer\":\"yes\"}]";
            var result = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.AreEqual(1, result.Qualifications.Count);
            var key = result.Qualifications.FirstOrDefault().Key;
            Assert.IsTrue(key.GetType() == typeof(Question));
            Assert.AreEqual(1, key.Id);
            Assert.AreEqual("Do you have a car?", key.Text);
            var answers = result.Qualifications.FirstOrDefault().Value;
            Assert.AreEqual(1, answers.Count);
            var answer = answers[0];
            Assert.IsNotNull(answer);
            Assert.AreEqual("yes", answer.Text);
        }

        [Test]
        public void GetJobFromJsonInputReturnCorrectJobInformation()
        {
            var result = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.AreEqual(2, result.Qualifications.Count);
            Assert.IsTrue(result.Qualifications.All(x => x.Value.TrueForAll(y => y.Text.Equals("yes"))));
        }

        [Test]
        public void GetJobApplicationsFromJsonInputReturnNullValueDueToNullOrEmptyParameter()
        {
            var job = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNotNull(job);
            var result = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, string.Empty);
            Assert.IsNull(result);
            result = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(null, jobApplicationsFromJson);
            Assert.IsNull(result);
        }

        [Test]
        public void GetJobApplicationsFromJsonInputReturnNullValueDueToWrongFormat()
        {
            var job = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNotNull(job);
            var jobApplicationsFromJson =
                "[Name:\"John Smith\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"yes\"},{\"Id\":\"2\",\"Answer\":\"yes\"}]},{\"Name\":\"John Doe\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"no\"},{\"Id\":\"2\",\"Answer\":\"yes\"}]},{\"Name\":\"Marie Smith\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"No\"},{\"Id\":\"2\",\"Answer\":\"No\"}]},{\"Name\":\"Paz Jensen\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"yes\"},{\"Id\":\"2\",\"Answer\":\"yes\"}]}]";
            var result = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, jobApplicationsFromJson);
            Assert.IsNull(result);

            jobApplicationsFromJson =
                "[{\"Name\":\"John Smith\",\"Questions\":[{\"Id\":\"ThisCausesAnExceptionSinceImNotANumber\",\"Answer\":\"yes\"},{\"Id\":\"2\",\"Answer\":\"yes\"}]},{\"Name\":\"John Doe\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"no\"},{\"Id\":\"2\",\"Answer\":\"yes\"}]},{\"Name\":\"Marie Smith\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"No\"},{\"Id\":\"2\",\"Answer\":\"No\"}]},{\"Name\":\"Paz Jensen\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"yes\"},{\"Id\":\"2\",\"Answer\":\"yes\"}]}]";
            Assert.That(() => _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, jobApplicationsFromJson), Throws.Nothing);
            result = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, jobApplicationsFromJson);
            Assert.IsNull(result);
        }

        [Test]
        public void GetJobApplicationsFromJsonInputReturnCorrectJobInformation()
        {
            var job = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNotNull(job);
            var result = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, jobApplicationsFromJson);
            Assert.AreEqual(4, result.Count);
            Assert.IsTrue(result.All(x => x.AnsweredQuestions.Count == 2));
            Assert.IsTrue(result.All(x => !string.IsNullOrEmpty(x.Applicant.Name)));

        }

        [Test]
        public void ValidateJobApplicationsReturnNullDueToNullParameter()
        {
            var results = _jobApplicationProcessor.ValidateJobApplications(null);
            Assert.IsNull(results);
        }

        [Test]
        public void ValidateJobApplicationsReturn2Results()
        {
            var job = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNotNull(job);
            var jobApplications = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, jobApplicationsFromJson);
            Assert.IsNotNull(jobApplications);
            var results = _jobApplicationProcessor.ValidateJobApplications(jobApplications);
            Assert.IsNotNull(results);
            Assert.AreEqual(2, results.Count);
            Assert.IsTrue(results.Exists(x => x.Name.Equals("John Smith")));
            Assert.IsTrue(results.Exists(x => x.Name.Equals("Paz Jensen")));
        }

        [Test]
        public void ValidateJobApplicationsReturnNoResults()
        {
            var job = _jobApplicationProcessor.GetJobFromJsonInput(jobInfoFromJson);
            Assert.IsNotNull(job);
            var jobApplicationsFromJson =
                "[{\"Name\":\"John Smith\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"no\"},{\"Id\":\"2\",\"Answer\":\"no\"}]},{\"Name\":\"John Doe\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"no\"},{\"Id\":\"2\",\"Answer\":\"no\"}]},{\"Name\":\"Marie Smith\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"No\"},{\"Id\":\"2\",\"Answer\":\"No\"}]},{\"Name\":\"Paz Jensen\",\"Questions\":[{\"Id\":\"1\",\"Answer\":\"no\"},{\"Id\":\"2\",\"Answer\":\"no\"}]}]";
            var jobApplications = _jobApplicationProcessor.GetJobApplicationsFromJsonInput(job, jobApplicationsFromJson);
            Assert.IsNotNull(jobApplications);
            var results = _jobApplicationProcessor.ValidateJobApplications(jobApplications);
            Assert.IsNotNull(results);
            Assert.AreEqual(0, results.Count);
        }

